function syntaxHighlight(elementId) {
  const payload = JSON.parse(document.getElementById(elementId).innerText);
  const string = JSON.stringify(payload, undefined, 4);

  document.getElementById(elementId).innerHTML = string
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;")
    .replace(
      /("(\\u[a-zA-Z0-9]{4}|\\[^u]|[^\\"])*"(\s*:)?|\b(true|false|null)\b|-?\d+(?:\.\d*)?(?:[eE][+\-]?\d+)?)/g,
      function (match) {
        let cls = "number";
        if (/^"/.test(match)) {
          if (/:$/.test(match)) {
            cls = "key";
          } else {
            cls = "string";
          }
        } else if (/true|false/.test(match)) {
          cls = "boolean";
        } else if (/null/.test(match)) {
          cls = "null";
        }
        return '<span class="' + cls + '">' + match + "</span>";
      }
    );
}

$(function () {
  $("button[name=goBack]").click(function (e) {
    e.preventDefault();
    history.back();
  });

  $("a.settings").click(function (e) {
    e.preventDefault();
    $(".more-info").slideToggle();
  });
});
